from PyGithub.Blocking.tests.Framework import *


class PublicKeyAttributes(TestCase):
    def test(self):
        k = self.user1.get_authenticated_user().get_key(3)
        self.assertEqual(k.id, 3)
        self.assertEqual(k.key, "ssh-rsa ")
        self.assertEqual(k.title, "key-1-2")
        self.assertEqual(k.verified, True)
        self.assertEqual(k.url, "http://github.home.jacquev6.net/api/v3/user/keys/3")  # @todoAlpha Test url of repository key


class PublicKeyDelete(TestCase):
    def test(self):
        k = self.user1.get_authenticated_user().create_key("key-1-3", "ssh-rsa ")
        self.assertEqual(k.title, "key-1-3")
        k.delete()


class PublicKeyUpdate(TestCase):
    def testArtifical(self):
        # Public keys are always returned completely so there is no other way to cover _updateAttributes
        k = self.user1.get_authenticated_user().get_key(3)
        k._updateAttributes(None, url=k.url)

    def test(self):
        # Public keys are immutable so there is no way update a complete object
        k = self.electra.get_authenticated_user().get_key(5)
        self.assertFalse(k.update())
